mapper.select:
SELECT seq, id, ref, step, depth, title, content, created_at createdAt, del, read_count readCount, b_like bLike FROM (SELECT row_number() OVER(ORDER BY ref DESC, step ASC) AS rnum, seq, id, ref, step, depth, title, content, created_at, del, read_count, b_like FROM board
WHERE title LIKE concat('%', #{search}, '%')
WHERE content LIKE concat('%', #{search}, '%')
WHERE id=#{search}
ORDER BY ref DESC, step ASC) a WHERE rnum BETWEEN ${pageStart} AND ${pageEnd}

mapper.update:
UPDATE board
SET read_count = read_count + 1
WHERE seq = #{seq};

mapper.insert:
INSERT board (id, ref, step, depth, title, content, created_at, del, read_count, b_like)
values (#{id}, (SELECT ref FROM board a WHERE seq=#{parentSeq}), (select ifnull(max(step), 0) + 1 from board b where ref=(select ref FROM board c WHERE seq=#{parentSeq})), (SELECT depth FROM board d WHERE seq=#{parentSeq}) + 1, #{title}, #{content}, NOW(), 0, 0, 0);
VALUES (#{id}, (SELECT ref FROM board e WHERE seq=#{parentSeq}), #{checkResult}, (SELECT depth FROM board f WHERE seq=#{parentSeq}) + 1, #{title}, #{content}, NOW(), 0, 0, 0);

mapper.update:
UPDATE board
SET content=#{content}, title=#{title}
WHERE seq=#{seq}

UPDATE board
SET del = 1
WHERE seq=#{seq}

UPDATE board
SET b_like = b_like + 1
WHERE seq = #{seq};

mapper.insert:
INSERT INTO custom_card_to_board(filepath, seq)
VALUES (#{filePath}, #{seq})
on duplicate key update filepath=#{filePath}, seq=#{seq}

mapper.select:
select filepath
from custom_card_to_board
where seq=#{seq}
